package org.example.duanxuong.api;import org.example.duanxuong.entity.Course;import org.example.duanxuong.entity.Topic;import org.example.duanxuong.service.CourseService;import org.example.duanxuong.service.TopicService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.HashMap;import java.util.Map;@RestController@RequestMapping("/course-api")public class CourseApi {    @Autowired    private CourseService courseService;    @GetMapping("/getAllCourse")    public ResponseEntity<?> doGetAllCourse() {        Map<String, Object> result = new HashMap<>();        result.put("success", true);        result.put("message", "Call Api Success");        result.put("data", courseService.getAllCourse());        return ResponseEntity.ok(result);    }    @PostMapping("/postCourse")    public ResponseEntity<?> doPostCourse(@RequestBody Course course) {        Map<String, Object> result = new HashMap<>();        try {            result.put("success", true);            result.put("message", "Call Api Success");            result.put("data", courseService.doSaveCourse(course));        } catch (Exception e) {            result.put("success", false);            result.put("message", "Call Api Error");            result.put("data", null);            e.printStackTrace();        }        return ResponseEntity.ok(result);    }    @DeleteMapping("/deleteByIdCourse/{id}")    public ResponseEntity<?> doDeleteByIdCourse(@PathVariable int id) {        Map<String, Object> result = new HashMap<>();        try {            result.put("success", true);            result.put("message", "Call Api Success");            courseService.doGetDeleteByIdCourse(id);        } catch (Exception e) {            result.put("Error", false);            result.put("message", "Call Api Error");            result.put("data", null);        }        return ResponseEntity.ok(result);    }}