package org.example.duanxuong.controller;import lombok.RequiredArgsConstructor;import org.example.duanxuong.entity.Mail;import org.example.duanxuong.service.MailService;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;import java.util.HashMap;import java.util.Map;@RequiredArgsConstructor@RestControllerpublic class MailApi {    private final MailService mailService;    @PostMapping("/sendMail")    public Map<?, ?> doPostSendMail() {        var resultMap = new HashMap<String, Object>();        try {            mailService.sendMail(new Mail());            resultMap.put("success", true);        } catch (Exception e) {            resultMap.put("success", false);            throw new RuntimeException(e);        }        return resultMap;    }    @PostMapping("/sendEmailCustomBody")    public Map<?, ?> doPostSendMailWithCustomBody(@RequestBody Mail mailModel) {        var resultMap = new HashMap<String, Object>();        try {            mailService.sendMail(mailModel);            resultMap.put("success", true);        } catch (Exception e) {            resultMap.put("success", false);            throw new RuntimeException(e);        }        return resultMap;    }}