package org.example.duanxuong.controller;import org.example.duanxuong.entity.User;import org.example.duanxuong.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import java.util.List;@Controllerpublic class UserController {    @Autowired    private UserService userService;    @GetMapping({"/", "/trang-chinh"})    public String ViewTrangChu(Model model) {        model.addAttribute("currentNav", "/");        return "user/home";    }    @GetMapping("/khoa-hoc")    public String ViewKhoaHoc(Model model) {        model.addAttribute("currentNav", "khoa-hoc");        return "user/course";    }    @GetMapping("/thong-tin-ca-nhan")    public String ViewThongTin() {        return "user/profile";    }    @GetMapping("/video")    public String ViewVideo() {        return "user/video-course";    }    @GetMapping("/list")    public String listUser (Model model){        List<User> list=userService.findAllUser();        model.addAttribute("users",list);        return "admin/user";    }}