package org.example.duanxuong.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;@Controllerpublic class UserController {    @GetMapping({"/", "/trang-chinh"})    public String ViewTrangChu(Model model) {        model.addAttribute("currentNav", "/");        return "user/home";    }    @GetMapping("/khoa-hoc")    public String ViewKhoaHoc(Model model) {        model.addAttribute("currentNav", "khoa-hoc");        return "user/course";    }    @GetMapping("/thong-tin-ca-nhan")    public String ViewThongTin() {        return "user/profile";    }    @GetMapping("/video")    public String ViewVideo() {        return "user/video-course";    }}