package org.example.duanxuong.controller;import lombok.RequiredArgsConstructor;import org.example.duanxuong.entity.*;import org.example.duanxuong.service.CourseDetailService;import org.example.duanxuong.service.CourseService;import org.example.duanxuong.service.RateService;import org.example.duanxuong.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.Banner;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import java.util.List;@Controller@RequiredArgsConstructorpublic class UserController {    private final UserService userService;    private final CourseService courseService;    private final CourseDetailService courseDetailService;    private final RateService rateService;    @GetMapping({"/", "/trang-chinh"})    public String ViewTrangChu(Model model) {        model.addAttribute("currentNav", "/");        return "user/home";    }    @GetMapping({"/404"})    public String View404() {        return "user/404";    }    @GetMapping("/khoa-hoc")    public String ViewKhoaHoc(Model model) {        List<Course> list = courseService.getAllCourse();        model.addAttribute("courses", list);        model.addAttribute("currentNav", "khoa-hoc");        return "user/course";    }    @GetMapping("/chi-tiet-khoa-hoc/{courseId}")    public String ViewChiTiet(@PathVariable("courseId") Integer id, Model model) {        CourseDetail courseDetail = courseDetailService.findById(id);        model.addAttribute("courseDetail", courseDetail);        return "user/course-detail";    }    @GetMapping("/thong-tin-ca-nhan")    public String ViewThongTin(Model model) {        model.addAttribute("currentNav", "thong-tin-ca-nhan");        return "user/profile";    }    @GetMapping("/video")    public String ViewVideo() {        return "user/video-course";    }    @GetMapping("/lien-he")    public String ViewContact(Model model) {        model.addAttribute("currentNav", "lien-he");        return "user/contact";    }    @GetMapping("/faq")    public String ViewFAQ(Model model) {        model.addAttribute("currentNav", "faq");        return "user/faq";    }}