package org.example.duanxuong.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.sql.Date;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "course")public class Course {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "course_id")    private int courseId;    @Column(name = "course_name", columnDefinition = "nvarchar(512)")    private String courseName;    @Column(name = "creation_date")    private Date creationDate;    @Column(name = "course_image")    private String courseImage;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "topic_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Topic topic;    @ManyToOne(fetch = FetchType.LAZY, cascade = {CascadeType.PERSIST, CascadeType.MERGE})    @JoinColumn(name = "instructor_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private User instructor;    @OneToOne(mappedBy = "course", fetch = FetchType.LAZY, cascade = CascadeType.ALL)    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private CourseDetail courseDetail;}