package org.example.duanxuong.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "course_detail")public class CourseDetail {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "course_detail_id")    private int courseDetailId;    @Column(name = "course_detail_name", columnDefinition = "nvarchar(512)")    private int courseDetailName;    @Column(name = "course_detail_desc", columnDefinition = "nvarchar(512)")    private String courseDetailDescription;    @OneToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "course_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Course course;}