package org.example.duanxuong.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.time.LocalDateTime;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "rate")public class Rate {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "rate_id")    private int rate_id;    @Column(name = "stars")    private int stars;    @Column(name = "comment", columnDefinition = "nvarchar(512)")    private String comment;    @Column(name = "comment_date")    private String commentDate;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "course_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Course course;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "user_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private User user;}