package org.example.duanxuong.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import com.fasterxml.jackson.annotation.JsonManagedReference;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "role")public class Role {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "role_id")    private int roleId;    @Column(name = "role_name")    private String roleName;    @Column(name = "role_desc")    private String roleDescription;    @OneToMany(mappedBy = "role", fetch = FetchType.LAZY)    @JsonManagedReference    private List<User> users;}