package org.example.duanxuong.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "topic")public class Topic {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "topic_id")    private int id;    @Column(name = "topic_name", columnDefinition = "nvarchar(512)")    private String name;    @Column(name = "image")    private String image;    @Column(name = "description", columnDefinition = "nvarchar(512)")    private String description;    @OneToMany(mappedBy = "topic", fetch = FetchType.LAZY, cascade = CascadeType.ALL)    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private List<Course> courseList;}