package org.example.duanxuong.repository;import org.example.duanxuong.entity.Rate;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import java.util.List;public interface RateRepository extends JpaRepository<Rate, Integer> {    @Query(value = "SELECT r FROM Rate r WHERE r.course.courseId = :courseId")    List<Rate> findByCourseId(@Param("courseId") int courseId);}