package org.example.duanxuong.repository;import org.example.duanxuong.entity.Role;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface RoleRepository extends JpaRepository<Role, Integer> {    @Query("SELECT r FROM Role r WHERE r.roleName LIKE?1")    List<Role> findByRoleName(String roleName);}