package org.example.duanxuong.service.impl;import org.example.duanxuong.entity.Course;import org.example.duanxuong.repository.CourseRepository;import org.example.duanxuong.service.CourseService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CourseServiceImpl  implements CourseService {    @Autowired    private CourseRepository courseRepository;    @Override    public List<Course> getAllCourse() {        return courseRepository.findAll();    }    @Override    public void doGetDeleteByIdCourse(int id) {        courseRepository.deleteById(id);    }    @Override    public Course doSaveCourse(Course course) {        return courseRepository.saveAndFlush(course);    }}