package org.example.duanxuong.service.impl;import org.example.duanxuong.entity.Rate;import org.example.duanxuong.repository.RateRepository;import org.example.duanxuong.service.RateService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class RateServiceImpl implements RateService {    @Autowired    private RateRepository rateRepository;    @Override    public List<Rate> getAllRate() {        return rateRepository.findAll();    }    @Override    public Rate doSaveRate(Rate rate) {        return rateRepository.save(rate);    }    @Override    public void doGetDelete(int id) {        rateRepository.deleteById(id);    }    @Override    public List<Rate> getRatesByCourseId(int courseId) {        return rateRepository.findByCourseId(courseId);    }}