package org.example.duanxuong.service.impl;import org.example.duanxuong.entity.Role;import org.example.duanxuong.repository.RoleRepository;import org.example.duanxuong.service.RoleService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class RoleServiceImpl implements RoleService {    @Autowired    private RoleRepository roleRepository;    @Override    public List<Role> getAllRole() {        return roleRepository.findAll() ;    }    @Override    public Role doSaveRole(Role role) {        return roleRepository.saveAndFlush(role);    }    @Override    public List<Role> getByRoleName(String roleName) {        return roleRepository.findByRoleName(roleName);    }    @Override    public void doGetDelete(int id) {        roleRepository.deleteById(id);    }    @Override    public List<Role> findByName(String roleName) {        return roleRepository.findByRoleName(roleName);    }}